  return (
    <div className="col-md-4 mb-4">
      <Link to={"food/" + id}>
        <div className="card text-center">
          <img src={img} alt="FoodItem" className="card-img-top" />

          <div className="card-body">
            <h5>{name}</h5>
            <p>{description}</p>
            <h4>â‚¹{price.toFixed(1) * 40}</h4>
          </div>
        </div>
      </Link>
    </div>
  );
};

export default FoodItem;
