  return (
    <div className="App">
      {showItem ? (
        <>
          <h2>Your card Details</h2>
          <StripeContainer />
        </>
      ) : (
        <>
          <h1>Total payable amount</h1>
          <h3>â‚¹{props.grandTotal}</h3>
          <button className="stripeButton" onClick={() => setShowItem(true)}>
            Make Payment
          </button>
        </>
      )}
    </div>
  );
}
